var StyleFilteredBox = [],
   LookId    =  function(id , code) {
      var id        =  '#'+id,
          position  =  code.indexOf(id);

      if(position !== -1){
         var targetId = code.substr(position , id.length);
         if(targetId == id){
            var BrokenCode    =  code.substr(position),
                BracketPosX   =  BrokenCode.indexOf('{'),
                BracketPosY   =  BrokenCode.indexOf('}'),
                FinalPosition =  BracketPosY - BracketPosX + 1,
                TargetStyle   =  id+BrokenCode.substr(BracketPosX , FinalPosition);
                if(TargetStyle.indexOf('{') !== -1 && TargetStyle.indexOf('}') !== -1 && TargetStyle.indexOf('#') !== -1 ){
                  StyleFilteredBox[id] = TargetStyle;
                  //StyleFilteredBox.push(TargetStyle);
                }
         }
      }
   },
   LookClass  =  function(clas , code) {
      
   },
   LookTag    =  function(tag , code) {
      
   },
   CssDecomposer = function(args , source) {
   var MyCode = source;
       for (let j = 0; j < args.length; j++) {
          var el     =  args[j],
              ID     =  el.id,
              CLASS  =  el.class,
              TAG    =  el.tag;
              
            if(ID){
               LookId(ID , MyCode)
            }else if(CLASS){
               var CLASS = CLASS.split(' ');
               LookClass(CLASS , MyCode);
            }else if(TAG){
               LookTag(TAG , MyCode)
            }
       }
},
AddComponentsStyles = function(data , AllData) {
   for (let j = 0; j < data.length; j++) {
      const el = data[j];
      if(el !== undefined){
         AllData  = AllData + el.styles;
      } 
   }
   
   return AllData;
};

